<?php	require_once("basicshapeabstract.class.php");	class Line extends BasicShapeAbstract{				function __construct($itemId){			$this->id = $itemId;			$this->shapeType = "line";		}				public function setCoordinates($x1, $x2, $y1, $y2){			$this->x1 = $x1; 			$this->x2 = $x2; 			$this->y1 = $y1; 			$this->y2 = $y2; 		}			}?>